      r={radius}
      fill='transparent'
      strokeWidth={strokeWidth}
      stroke={color}
      strokeDasharray={dashArray}
      strokeDashoffset={offset}
      transform={`rotate(${angle} ${cx} ${cy})`}
    >
      {animate ? (
        <animateTransform
          attributeName='transform'
          attributeType='XML'
          type='rotate'
