  type Challenge = {
    _id: string;
    name: string;
    instructions: string;
    imageUrl: string;
    pourcentage: number;
  };

export default function Challenge(props: Challenge) {

  const { name, instructions, imageUrl, pourcentage } = props;
  return (
    // <div className="min-h-screen py-6 flex flex-col justify-center">
