import React, { useState, useEffect } from 'react';
import Axios from '../axios-config';

  type Challenge = {
    _id: string;
    name: string;
    instructions: string;
    imageUrl: string;
  };

export default function Challenge(props: Challenge) {

  const { name, instructions, imageUrl } = props;
  return (
    <div className="Challenge">
      <div className="Name">
        <h2> {name} </h2>
      </div>
      <div className="instructions">
        <p> {instructions} </p>
      </div>
      <div className="image">
        <img src={imageUrl} />
      </div>
    </div>
  );

}

