import { signInFunctionParams } from '../types';
/**
  *@public
  *@function
  *@name useAuth
  */
declare function useAuth(): {
    authHeader: () => (string | null);
    signIn: (signInConfig: signInFunctionParams) => boolean;
    signOut: () => (boolean);
    isAuthenticated: () => (boolean);
    authUser: () => (object | null);
};
/**
  *@exports useAuth
  */
export default useAuth;
